<script>
    const shortcut_NavBtn = document.querySelector('#shortcut-NavBtn')
    const shortcut_Nav = document.querySelector('#shortcut-Nav')

    shortcut_NavBtn.addEventListener('click', ()=>{
        if(shortcut_Nav.classList.contains('hidden'))
        {
            shortcut_Nav.classList.remove('hidden')
        }
        else
        {
            shortcut_Nav.classList.add('hidden')
        }
    });
</script>
